import React, { useEffect, useState } from "react";

function App() {
  const [indices, setIndices] = useState([]);
  const [gainers, setGainers] = useState([]);

  useEffect(() => {
    // Fetch market indices
    fetch("http://localhost:8000/market/indices")
      .then((res) => res.json())
      .then((data) => setIndices(data.indices || []))
      .catch(() => setIndices([]));

    // Fetch top gainers
    fetch("http://localhost:8000/market/top-gainers")
      .then((res) => res.json())
      .then((data) => setGainers(data || []))
      .catch(() => setGainers([]));
  }, []);

  // Basic example navigation handlers; replace with React Router if needed
  const goToLogin = () => (window.location.href = "/login");
  const goToSignUp = () => (window.location.href = "/signup");
  const goToCreateAccount = () => (window.location.href = "/signup");

  return (
    <div className="bg-dashboard min-h-screen">
      {/* Navbar */}
      <nav className="bg-gray-900 text-white flex justify-between items-center p-4">
        <span className="text-xl font-bold">USK BROKERING</span>
        <div>
          <button onClick={goToLogin} className="mr-4">
            Login
          </button>
          <button onClick={goToSignUp}>Register</button>
        </div>
      </nav>

      {/* Welcome & call to actions */}
      <div className="flex flex-col items-center mt-8 px-4">
        <div className="bg-white rounded-lg shadow-lg p-10 w-full max-w-3xl text-center">
          <h1 className="text-blue-600 text-3xl font-bold mb-2">
            Welcome to USK BROKERING
          </h1>
          <p className="mb-7">
            Professional trading platform with real-time market data, advanced
            order management, and comprehensive portfolio tracking.
          </p>
          <div className="flex justify-center space-x-4 mb-6">
            <button
              onClick={goToLogin}
              className="bg-purple-700 text-white px-5 py-2 rounded font-semibold"
            >
              Login
            </button>
            <button
              onClick={goToSignUp}
              className="border border-blue-500 px-5 py-2 rounded text-blue-500 font-semibold"
            >
              Sign Up
            </button>
          </div>

          {/* Features */}
          <div className="grid grid-cols-1 sm:grid-cols-4 gap-4">
            <Feature
              title="Real-time Trading"
