import React, { useEffect, useState } from "react";

function App() {
  const [isLogin, setIsLogin] = useState(true);
  const [loginForm, setLoginForm] = useState({ username: "", password: "" });
  const [registerForm, setRegisterForm] = useState({
    username: "",
    password: "",
  });
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [username, setUsername] = useState("");
  const [error, setError] = useState("");
  const [notification, setNotification] = useState("");
  const [indices, setIndices] = useState([]);
  const [gainers, setGainers] = useState([]);

  // Connect WebSocket for notifications
  useEffect(() => {
    if (token) {
      fetchUser();
      const ws = new WebSocket("ws://localhost:8000/ws/notifications");
      ws.onmessage = (event) => setNotification(event.data);
      ws.onclose = () => console.log("WebSocket disconnected");
      return () => ws.close();
    }
  }, [token]);

  // Fetch user profile
  async function fetchUser() {
    try {
      const res = await fetch("http://localhost:8000/users/me", {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error();
      const data = await res.json();
      setUsername(data.username);
    } catch {
      setToken("");
      localStorage.removeItem("token");
    }
  }

  // Market data fetch
  useEffect(() => {
    fetch("http://localhost:8000/market/indices")
      .then((res) => res.json())
      .then((d) => setIndices(d.indices || []))
      .catch(() => setIndices([]));
    fetch("http://localhost:8000/market/top-gainers")
      .then((res) => res.json())
      .then((d) => setGainers(d || []))
      .catch(() => setGainers([]));
  }, []);

  // Login handler
  async function handleLogin() {
    setError("");
    try {
      const res = await fetch("http://localhost:8000/login", {
        method: "POST",
        headers: { "Content-Type": "application/x-www-form-urlencoded" },
        body: new URLSearchParams(loginForm),
      });
      if (!res.ok) throw new Error("Invalid credentials");
      const data = await res.json();
      setToken(data.access_token);
      localStorage.setItem("token", data.access_token);
    } catch (e) {
      setError(e.message);
    }
  }

  // Register handler
  async function handleRegister() {
    setError("");
    try {
      const res = await fetch("http://localhost:8000/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(registerForm),
      });
      if (!res.ok) throw new Error(await res.text());
      alert("Registration successful! Please login.");
      setIsLogin(true);
    } catch (e) {
      setError(e.message);
    }
  }

  // Logout handler
  function logout() {
    setToken("");
    setUsername("");
    localStorage.removeItem("token");
  }

  // Handle input changes
  function onInputChange(e, formSetter) {
    formSetter((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  }

  if (!token)
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
        <div className="max-w-sm bg-white p-8 rounded shadow w-full">
          <h2 className="text-center text-2xl font-bold mb-6">
            {isLogin ? "Login" : "Register"}
          </h2>
          {error && <p className="text-red-600 mb-4">{error}</p>}

          {isLogin ? (
            <>
              <input
                name="username"
                placeholder="Username"
                value={loginForm.username}
                onChange={(e) => onInputChange(e, setLoginForm)}
                className="w-full p-2 border mb-4 rounded"
              />
              <input
                name="password"
                type="password"
                placeholder="Password"
                value={loginForm.password}
                onChange={(e) => onInputChange(e, setLoginForm)}
                className="w-full p-2 border mb-6 rounded"
              />
              <button
                onClick={handleLogin}
                className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition mb-4"
              >
                Login
              </button>
              <p className="text-center text-sm text-gray-600">
                Don't have an account?{" "}
                <button
                  onClick={() => setIsLogin(false)}
                  className="text-blue-600 underline"
                >
                  Register
                </button>
              </p>
            </>
          ) : (
            <>
              <input
                name="username"
                placeholder="Username"
                value={registerForm.username}
                onChange={(e) => onInputChange(e, setRegisterForm)}
                className="w-full p-2 border mb-4 rounded"
              />
              <input
                name="password"
                type="password"
                placeholder="Password"
                value={registerForm.password}
                onChange={(e) => onInputChange(e, setRegisterForm)}
                className="w-full p-2 border mb-6 rounded"
              />
              <button
                onClick={handleRegister}
                className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700 transition mb-4"
              >
                Register
              </button>
              <p className="text-center text-sm text-gray-600">
                Already have an account?{" "}
                <button
                  onClick={() => setIsLogin(true)}
                  className="text-blue-600 underline"
                >
                  Login
                </button>
              </p>
            </>
          )}
        </div>
      </div>
    );

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      <nav className="bg-gray-900 text-white px-6 py-4 fixed w-full flex justify-between items-center shadow z-50">
        <span className="font-bold text-xl">USK BROKERING</span>
        <div className="text-sm flex items-center space-x-4">
          <span>Welcome, {username}</span>
          <button
            onClick={logout}
            className="bg-red-600 px-3 py-1 rounded hover:bg-red-700 transition"
          >
            Logout
          </button>
        </div>
      </nav>

      <main className="pt-24 px-6 max-w-7xl mx-auto w-full flex-grow">
        <div className="bg-yellow-100 text-yellow-700 px-4 py-3 rounded mb-4">
          {notification || "No new notifications"}
        </div>
        {/* Your dashboard content here */}
      </main>
    </div>
  );
}

export default App;
