import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [forgot, setForgot] = useState(false); // Tracks Forgot Password UI
  const [email, setEmail] = useState("");
  const [forgotMsg, setForgotMsg] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch("http://localhost:8000/login", {
        method: "POST",
        headers: { "Content-Type": "application/x-www-form-urlencoded" },
        body: new URLSearchParams({ username, password }),
      });

      const data = await res.json();

      if (res.ok) {
        localStorage.setItem("token", data.access_token);
        navigate("/");
      } else {
        setError(data.detail || "Login failed");
      }
    } catch (err) {
      setError("Failed to connect to server");
    }
  };

  // Fake forgot handler (for demonstration)
  const handleForgot = async (e) => {
    e.preventDefault();
    // Typically you'd POST to backend endpoint
    setForgotMsg(
      "If your email is registered, you'll receive reset instructions."
    );
    setEmail("");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="bg-white p-8 rounded shadow-md w-full max-w-md">
        {!forgot ? (
          <form onSubmit={handleLogin} className="w-full">
            <h2 className="text-2xl font-bold mb-6 text-center">Login</h2>
            {error && <p className="text-red-600 mb-4">{error}</p>}
            <input
              type="text"
              placeholder="Username"
              className="border w-full mb-4 p-2 rounded"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              className="border w-full mb-4 p-2 rounded"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button
              type="submit"
              className="bg-blue-600 text-white rounded py-2 w-full font-semibold hover:bg-blue-700 transition mb-3"
            >
              Log In
            </button>
            <div className="flex justify-between text-sm mt-2">
              <button
                type="button"
                className="text-blue-600 hover:underline"
                onClick={() => setForgot(true)}
              >
                Forgot password?
              </button>
              <button
                type="button"
                className="text-blue-600 hover:underline"
                onClick={() => navigate("/signup")}
              >
                Register
              </button>
            </div>
          </form>
        ) : (
          <form onSubmit={handleForgot} className="w-full">
            <h2 className="text-xl font-bold mb-6 text-center">
              Forgot Password
            </h2>
            {forgotMsg && <p className="text-green-600 mb-4">{forgotMsg}</p>}
            <input
              type="email"
              placeholder="Enter your registered email"
              className="border w-full mb-4 p-2 rounded"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <button
              type="submit"
              className="bg-purple-600 text-white rounded py-2 w-full font-semibold hover:bg-purple-700 transition mb-3"
            >
              Send Reset Link
            </button>
            <button
              type="button"
              className="text-blue-600 hover:underline w-full text-center"
              onClick={() => {
                setForgot(false);
                setForgotMsg("");
              }}
            >
              Back to Login
            </button>
          </form>
        )}
      </div>
    </div>
  );
}

export default Login;
